mapper.select:
select distinct t.id menuId, t.* from be_menu t
left join be_menu p on t.id=p.parent
where t.sys005='1'

    and (t.menu_text like concat('%',#{menuText},'%') or p.menu_text like concat('%',#{menuText},'%'))


    and t.`enable` = #{enable}


    and t.`show` = #{show}


    and t.`menu_type` = #{menuType}


    and t.`app_id` = #{appId} and t.menu_type > '0'


    and t.`parent` = #{parent}

order by t.menu_type asc, t.sort_num asc

select t.* from be_menu t
where t.sys005='1' and level = '0'
and app_id = #{appId}

select t.* from be_menu t
where t.sys005='1' and parent = #{parent}
and sort_num < #{sortNum}
order by sort_num desc
limit 1

select t.* from be_menu t
where t.sys005='1' and parent = #{parent}
and sort_num > #{sortNum}
order by sort_num asc
limit 1
